import React from "react";
import Item from "../item/item.component"
import "./item-preview.styles.scss";
import {connect } from 'react-redux';
import {selectItemsForPreview} from '../../redux/shop/shop.selectors';
const ItemPreview = ({title, items}) => {
    return (<div className="item-preview"> 
        <h1 className="title">{title.toUpperCase()}</h1>
        <div className="preview">
            {items.filter((item, index) => index < 5).map((item) => {
                return <Item key={item.id} item={item}/>
            })}
        </div>
    </div>)
}
const mapStateToProps = (state, ownProps)=> ({
    itemsx: selectItemsForPreview(ownProps.match.params.categoryId)(state),
});
export default connect(mapStateToProps)(ItemPreview);
