import { createSelector } from "reselect";

const selectShop = (state) => state.shop;

export const selectInventory = createSelector(
  [selectShop],
  (shop) => shop.inventory
);

export const selectCategory = (categoryUrlParam) =>
  createSelector([selectInventory], (inventory) => inventory[categoryUrlParam]);

export const selectInventoryForPreview = createSelector(
  [selectInventory],
  (inventory) => Object.keys(inventory).map((key) => inventory[key])
);

export const selectItem = (categoryitemUrlParam) =>
  createSelector([selectInventory], (category) => category[itemUrlParam]);

export const selectItemForPreview = createSelector(
  [selectCategory],
  (category) => Object.keys(category).map((key) => category[key])
);
