import { createSelector } from "reselect";

const selectShop = (state) => state.shop;

export const selectInventory = createSelector(
  [selectShop],
  (shop) => shop.inventory
);

export const selectCategory = (categoryUrlParam) =>
  createSelector([selectInventory], (inventory) => inventory[categoryUrlParam]);

export const selectInventoryForPreview = createSelector(
  [selectInventory],
  (inventory) => Object.keys(inventory).map((key) => inventory[key])
);

export const selectItem = (categoryUrlParams, itemUrlParam) =>
  createSelector([selectInventory], (inventory) => inventory[categoryUrlParams][itemUrlParam]);

export const selectItemsForPreview = (categoryUrlParam) => createSelector(
  [selectCategory],
  (category) => Object.keys(category.items).map((key) => category.items[key])
);
