import React from 'react';
import {Link} from 'react-router-dom';
import { connect } from 'react-redux';

import {auth} from '../../firebase/firebase.utils';
import CartIcon from '../cart-icon/cart-icon.component';
import CartDropDown from '../cart-dropdown/cart-dropdown.component';
import {createStructuredSelector} from 'reselect';
import {selectCartHidden} from '../../redux/cart/cart.selectors';
import {selectCurrentUser} from '../../redux/user/user.selectors';
import NavBar from '../navbar/navbar.component';
import {HeaderContainer, LogoContainer, OptionsContainer, OptionDiv, OptionLink} from './header.styles.jsx'
const Header = ({currentUser, hidden}) => (
    <HeaderContainer>
        <Link to="/">
            <LogoContainer src="https://i.etsystatic.com/isla/e7a1a1/40347398/isla_75x75.40347398_nvu4bmdm.jpg?version=0"/>
        </Link>
        <NavBar/>
        <OptionsContainer>
            <OptionLink to="/shop">SHOP</OptionLink>
            <OptionLink className="option"to="/contact">CONTACT</OptionLink>
            {
                currentUser ? <OptionDiv className="option" onClick={() => auth.signOut()}>SIGN OUT</OptionDiv> : <Link className="option" to="/signIn">SIGN IN</Link>
            }
            <CartIcon/>
        </OptionsContainer>
        {hidden ? null : <CartDropDown/>}
    </HeaderContainer>
);

const mapStatetoProps = createStructuredSelector({
   currentUser: selectCurrentUser,
   hidden: selectCartHidden
})

export default connect(mapStatetoProps)(Header);
