import { createSelector } from "reselect";
import { selectCartItems } from "../cart/cart.selectors";

const selectShop = (state) => state.shop;

export const selectCollections = createSelector(
  [selectShop],
  (shop) => shop.collections
);

export const selectCollection = (collectionUrlParam) =>
  createSelector([selectCollections], (collections) => collections[collectionUrlParam]);

export const selectCollectionsForPreview = createSelector(
  [selectCollections],
  (collections) => Object.keys(collections).map(key => collections[key])
);

export const selectCategoryForPreview = (categoryUrlParam) =>
  createSelector([selectCollections], (collections) => Object.keys(collections[categoryUrlParam].items).map(key => collections[categoryUrlParam].items[key]));


export const selectItem = (categoryUrlParam, itemUrlParam) => createSelector(
  [selectCollections],
  (collections) => collections[categoryUrlParam].items[itemUrlParam]
)
