import React from 'react';
import './collection.styles.scss';
import Item from '../../components/item/item.component';
import { connect } from 'react-redux';
import {selectCollection, selectCollectionForPreview} from '../../redux/shop/shop.selectors';
const CollectionPage = ({collection, collectionArray}) => {
    const {title} = collection;
    return (<div className='collection-page'>
        <h2 className='title'>{title.toUpperCase()}</h2>
        <div className='items'>
            {collectionArray.map(item => {
                return <Item className="item" key={item.id} item={item}/>
            })}
        </div>
    </div>)
}

const mapStateToProps = (state, ownProps)=> ({
    collection: selectCollection(ownProps.match.params.collectionId)(state),
    collectionArray: selectCollectionForPreview("desk-buddy")(state)
})
export default connect(mapStateToProps)(CollectionPage);
