import React from 'react';

import "./item.styles.scss";

const Item = ({id, name, price, imageUrl}) => (
    <div className="item">
        <div className="image"
        style={{
            backgroundImage: `url(${imageUrl})`}}>
        </div>
        <div className="item-footer">
            <span className="name">{name}</span>
            <span className="price">{price}</span>
        </div>
    </div>
)
export default Item;
