import { createSelector } from "reselect";

const COLLECTION_ID_MAP = {
  earrings: 1,
  necklace: 2,
  desk_buddy: 3,
  polymer_clay_charms: 4,
  resin_charms_keychain: 5,
};
const selectShop = (state) => state.shop;

export const selectInventory = createSelector(
  [selectShop],
  (shop) => shop.inventory
);

export const selectCategory = (categoryUrlParam) => {
  createSelector([selectInventory], (inventory) =>
    inventory.find(
      (category) => category.id === COLLECTION_ID_MAP[categoryUrlParam]
    )
  );
};
